/**
 * Basic workflow module for entries.
 *
 * @package		Draftee
 * @subpackage	ThirdParty
 * @category	Modules
 * @author		Iain
 * @link		http://localhost:8888/draftee/
 */
class Draftee_upd {
		
	var $version        = '0.001'; 
	var $module_name = "Draftee";
	
    function Draftee_upd( $switch = TRUE ) 
    { 
		// Make a local reference to the ExpressionEngine super object
		$this->EE =& get_instance();
    } 

    /**
     * Installer for the Draftee module
     */
    function install() 
	{				
						
		$data = array(
			'module_name' 	 => $this->module_name,
			'module_version' => $this->version,
			'has_cp_backend' => 'y'
		);

		$this->EE->db->insert('modules', $data);		
		
		//
		// Add additional stuff needed on module install here
		// 
		
		$this->EE->load->dbforge();
		
		// main table for tracking drafts.
		$fields = array(
			            'id' => array(	'type' => 'int',
			            				'constraint' => '10', 
			            				'unsigned' => TRUE, 
			            				'auto_increment' => TRUE),

			            'parent_id' 	 => array(	'type' => 'int', 
			            						'constraint' => '10', 
			            						'unsigned' => TRUE),

			            'draft_id' 	 => array(	'type' => 'int', 
			            						'constraint' => '10', 
			            						'unsigned' => TRUE),
			            						
			            'pushed' 	 => array(	'type' => 'int', 
			            						'constraint' => '1', 
			            						'unsigned' => TRUE,
			            						'default' => 0)
			        );
			        
		$this->EE->dbforge->add_field($fields);
		$this->EE->dbforge->add_key('id', TRUE);
		$this->EE->dbforge->create_table('draftee_drafts');
																									
		return TRUE;
	}

	
	/**
	 * Uninstall the Draftee module
	 */
	function uninstall() 
	{ 				
		
		$this->EE->db->select('module_id');
		$query = $this->EE->db->get_where('modules', array('module_name' => $this->module_name));
		
		$this->EE->db->where('module_id', $query->row('module_id'));
		$this->EE->db->delete('module_member_groups');
		
		$this->EE->db->where('module_name', $this->module_name);
		$this->EE->db->delete('modules');
		
		$this->EE->db->where('class', $this->module_name);
		$this->EE->db->delete('actions');
		
		$this->EE->db->where('class', $this->module_name.'_mcp');
		$this->EE->db->delete('actions');
										
		return TRUE;
	}
	
	/**
	 * Update the Draftee module
	 * 
	 * @param $current current version number
	 * @return boolean indicating whether or not the module was updated 
	 */
	
	function update($current = '')
	{
		return FALSE;
	}

    /** @return Devkit_code_completion_helper */ function EE() {if(!isset($this->EE)){$this->EE =& get_instance();}return $this->EE;}    
}

